const { FETCH_ALL} = require("../actions/appStateTypes");


const initState = {
    posts: [],
    errors: null,
    isLoading: false,
    
}
export default (state=initState, action) => {
  switch (action.type) {
    case "FETCH_ALL":
      return action.payload;
    default:
      return posts;
  }
};
