const { GET_POST_SUCCESS, GET_POST_COUNT_SUCCESS, GET_MY_POST_SUCCESS} = require("../actions/appStateTypes");


const initState = {
    posts: [],
    errors: null,
    isLoading: false,
    
}
export default (posts = [], action) => {
  switch (action.type) {
    case "FETCH_ALL":
      return action.payload;
    default:
      return posts;
  }
};
