    // Do something with currentLocation
  })

  useEffect(() => {
    if (location) {
      setCurrentLocation(location.pathname)
      dispatch(getPosts() );
    setLoading(true);
    } else {
      setCurrentLocation('')
    }
    
  }, [dispatch, search]);
  return (
    <>
      <Header />
      {loading ? (
        <div className="home">
          {Array.isArray(posts) && posts.length > 0 ? (
            <Posts posts={posts} />
          ) : null}
          <SideBar />
        </div>
      ) : null}
    </>
  );
}
