    dispatch(getPosts() + sear);
    setLoading(true);
  }, [dispatch, search]);
  return (
    <>
      <Header />
      {loading ? (
        <div className="home">
          {Array.isArray(posts) && posts.length > 0 ? (
            <Posts posts={posts} />
          ) : null}
          <SideBar />
        </div>
      ) : null}
    </>
  );
}
