const initState = {
    posts: [],
    errors: null,
    isLoading: false,
    count: 0
}



 const postReducer = (state=initState,action) =>{
    switch(action.type) {
        case "FETCH_ALL":
            return {
                ...state,
                posts : action.payload,
            }
        default:
            return state;

    } 
}
export default postReducer;
