import React from "react";
import {useSelector} from 'react-redux';
import Post from "../post/Post";
import "./posts.css";C
export default function Posts({set}) {
  const posts = useSelector((state)=> state.posts)
  
  
  return (
    <div className="posts">
      {posts.map((p, id) => (
         <Post key={id} post={p} />
      ))}
      
    </div>
  );
}
