export const initialState = {
    posts: [],
    loading: false,
    hasErrors: false,
  }
export default function postsReducer(posts = [], action)  {
  switch (action.type) {
    case "FETCH_ALL":
      return action.payload;
    default:
      return posts;
  }
};
